INSERT INTO db_script_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000001910,'Bring forth the blood elf.',0,0,0,1,NULL),
(2000001911,'You heard the Prophet. Move, filth.',0,0,0,25,NULL),
(2000001912,'Matis, blood elf lieutenant of the Sunhawks, you have been found guilty of genocide against the draenei people.',0,0,0,1,NULL),
(2000001913,'You are to be executed at light\'s dawning. Have you anything to say for yourself?',0,0,0,1,NULL),
(2000001914,'%s spits at the image of Velen.',0,2,0,273,NULL),
(2000001915,'May the Light have mercy on your soul. Take him back to his cell.',0,0,0,1,NULL),
(2000001916,'%s glares at Velen.',0,2,0,0,NULL),
(2000001917,'The Light?',0,0,0,6,NULL),
(2000001918,'%s laughs.',0,2,0,11,NULL),
(2000001919,'I am Blood Knight, mongrel. I do not serve the Light, the Light serves me. If mercy is to be granted, it will be by me.',0,0,0,25,NULL),
(2000001920,'You seek to martyr me before my order - so be it. Strike me down, another will rise in my place and the suffering of the Light - of the conquered - will continue uninterrupted.',0,0,0,1,NULL),
(2000001921,'You blaspheme! The Light would never allow such an aberration!',0,0,0,25,NULL),
(2000001922,'The Light has no choice. It does as it is commanded. Just as the naaru you hold at the core of your vessel will do when we capture and enslave it... Do my words surprise you, blue blood? Yes, we know... We know everything.',0,0,0,1,NULL),
(2000001923,'Take him away.',0,0,0,25,NULL),
(2000001924,'Move!',0,0,0,5,NULL),
(2000001925,'Draenei dog! Did you know that the one you call Saruan wept like a babe as I beat him? A feeling of euphoria coursed through me, knowing that I had a member of high rank in my grasp. Were it not for Sironas\' plans for him, I would have tortured him to death... As I do to all draenei that I capture. Alas, I am certain that you will see him again soon...',0,0,0,5,NULL),
(2000001926,'%s grins wickedly.',0,2,0,0,NULL),
(2000001927,'Breathe your last breath, heathen.',0,0,0,25,NULL),
(2000001928,'%s spits on the corpse of Matis.',0,2,0,15,NULL),
(2000001929,'Find their source of power and tear it down. Tear all of it down.',0,0,0,1,NULL);
