INSERT IGNORE INTO `npc_text` VALUES ('14438', 'Well. Another $c, come to learn the ways of the Shield-Breaker...?', '', '0', '1', '0', '6', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0');
INSERT IGNORE INTO `npc_text` VALUES ('14439', 'The ranged Shield-Breaker attack is simple to learn, but difficult to master. Alone it may seem weak, but in conjunction with Thrusts and Charges the Shield-Breaker makes a successful jouster.$B$BFind a Ranged Target. Take a moment to understand the Shield-Breaker\'s range... how much space you need, and how far you can hurl the lance.$B$BAfter that? Well. Start throwing, and do not stop until you\'ve broken every last layer of Defend and pierced the vulnerable target.$B$BThis tactic will serve you well in actual mounted combat: Break down your opponents\' defenses... and then crush them!', '', '0', '1', '0', '1', '3', '5', '2', '11', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0', null, null, '0', '0', '0', '0', '0', '0', '0', '0');
