# GOLD
CREATE TABLE `_gold` (
  `entry` mediumint(8) unsigned NOT NULL default '0',
  `mingold` mediumint(8) unsigned NOT NULL default '0',
  `maxgold` mediumint(8) unsigned NOT NULL default '0',
  PRIMARY KEY  (`entry`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
update creature_template set mingold=(select mingold from _gold where _gold.entry=creature_template.entry) where entry in (select entry from _gold);
update creature_template set maxgold=(select maxgold from _gold where _gold.entry=creature_template.entry) where entry in (select entry from _gold);
drop table _gold;


